
public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int shritsize = 90;
switch(shritsize) {
case 40:
System.out.println("My size is M");
break;
case 42:
	System.out.println("My Size is L");
	break;
case 44:
	System.out.println("My size is XL");
	break;
case 46:
	System.out.println("My size is XXL");
	break;
	
case 48:
System.out.println("My size is XXXL");
break;
case 38:
	System.out.println("My size is small");
	break;
	
default :
	System.out.println("We dont have sizes");
	break;
}

	}

}
